mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.role_types as roleTypes
FROM xinzi a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.yonghu_id = #{params.yonghuId}
and a.month like CONCAT('%',#{params.month},'%')
and a.money >= #{params.moneyStart}
and a.money <= #{params.moneyEnd}
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.role_types = #{params.roleTypes}
order by a.${params.orderBy} desc
